    Total1 = lists:sum([ score1(X) || X <- Variants ]),
    io:fwrite("~w~n", [Total1]),
    Total2 = lists:sum([ score2(X) || X <- Variants ]),
    io:fwrite("~w~n", [Total2]).


% -------- part 1 ---------

% opponent
shape_score(a) -> 1;
shape_score(b) -> 2;
shape_score(c) -> 3;
% you
shape_score(x) -> 1;
shape_score(y) -> 2;
shape_score(z) -> 3;
% _
shape_score(_) -> badinput.

winner({3, 1})                     -> you; 
winner({1, 3})                     -> opp; 
winner({Opp, You}) when You > Opp  -> you;
winner({Opp, You}) when You < Opp  -> opp;
winner({Opp, You}) when You == Opp -> draw.

score1({Opp, You}) ->
    Bonus = case winner({shape_score(Opp), shape_score(You)}) of
        you  -> 6;
        opp  -> 0;
        draw -> 3
    end,
    shape_score(You) + Bonus.

% -------- part 2 ---------
% to lose
outcome({1, x}) -> 3;
outcome({Opp, x}) -> Opp-1;
% to draw
outcome({Opp, y}) -> Opp;
% to win
outcome({3, z}) -> 1;
outcome({Opp, z}) -> Opp+1.

score2({Opp, You}) ->
    YourChoose = outcome({shape_score(Opp), You}),
    Bonus = case winner({shape_score(Opp), YourChoose}) of
        you  -> 6;
        opp  -> 0;
        draw -> 3
    end,
    YourChoose + Bonus.
