-module(d06).
-export([run/0]).

run() ->
    Input = "",
    % part 1
    TunedOffset = scan(Input, 4, 0),
    io:fwrite("~w~n", [TunedOffset]),
    % part 2
    MsgOffset = scan(Input, 14, 0),
    io:fwrite("~w~n", [MsgOffset]).

scan(Input, WindowSize, Offset) ->
    Window = lists:sublist(Input, Offset + 1, WindowSize),
    case length(lists:uniq(Window)) of
        WindowSize -> Offset + WindowSize;
        _ -> scan(Input, WindowSize, Offset + 1)
    end.
